mapper.update:
UPDATE scrape_jobs
SET status = #{status},
    error_message = #{errorMessage}
WHERE id = #{id}

UPDATE scrape_jobs
SET scraped_count = #{scrapedCount}
WHERE id = #{id}

mapper.select:
SELECT id, status, total_categories, scraped_count, error_message, started_at, completed_at, created_at
FROM scrape_jobs
WHERE id = #{id}
